import requests
import json
import string

def get_travelodges():
    def search_name(name):
        response = requests.get("https://www.travelodge.co.uk/api/v2/places/autosuggestion?q=" + name)
        a = json.loads(response.text)
        return a["results"]


    travelodges = []
    for c1 in string.ascii_lowercase:
        for c2 in string.ascii_lowercase:
            results = search_name(c1 + c2)
            print(c1 + c2)
            if len(results) > 0:
                for c3 in string.ascii_lowercase:
                    results = search_name(c1 + c2 + c3)
                    for result in results:
                        if result["type"] == "hotel":
                            travelodges.append(result["title"])

    travelodges = sorted(set(travelodges))
    print(travelodges)

